import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginComponent = () => {
  const [clientId, setClientId] = useState("");
  const useNavigate = useNavigate();

  useEffect(() => {
    // 當組件加載時，從後端獲取 Google 客戶端 ID。
    axios
      .get("http://localhost:3005/auth/config/google")
      .then((response) => {
        setClientId(response.data.clientId);
      })
      .catch((error) => {
        console.error("錯誤發生: ", error);
      });

    const hash = window.location.hash.substr(1);
    const result = hash.split("&").reduce((result, item) => {
      let parts = item.split("=");
      result[parts[0]] = parts[1];
      return result;
    }, {});

    if (result.token) {
      localStorage.setItem("authToken", result.token);
      history.push("/"); // token已經保存，重定向到主頁
    }
  }, []); // 依賴於 location，當 location 改變時，此 useEffect 將重新運行

  const handleLogin = () => {
    // 重定向到 Google 的 OAuth 2.0 登錄頁面。
    window.location.href = "http://localhost:3005/auth/google";
  };

  return (
    <div>
      {clientId ? (
        <button
          className="btn btn-lg btn-google" // 注意：應該使用 className 而不是 class
          style={{
            padding: "0.2rem 0.5rem",
            backgroundColor: "rgb(7, 7, 7)",
            color: "rgb(255, 255, 255)",
          }}
          onClick={handleLogin} // 確保 onClick 被正確設置
        >
          <img
            src="https://img.icons8.com/color/16/000000/google-logo.png"
            alt="Google logo"
          />
          使用 Google 登入
        </button>
      ) : (
        <div>加載中...</div>
      )}
    </div>
  );
};

export default LoginComponent;
