import React from "react";
import { GoogleOAuthProvider, useGoogleLogin } from "@react-oauth/google";


const LoginButton = () => {
    const { signIn } = useGoogleLogin(); // 使用提供的 signIn 函数

    return <button onClick={signIn}>Login with Google</button>; // 当按钮点击时触发 signIn 函数
};

const LoginComponent = () => {
    // 成功回调函数
    const handleSuccess = (response) => {
        console.log('Login successful', response);
        // 此处可以进行后续操作，例如状态更新、重定向等
    };

    // 失败回调函数
    const handleFailure = (error) => {
        console.error('Login failed', error);
        // 此处可以处理登录失败，例如弹出错误提示
    };

    return (
        // 使用 GoogleOAuthProvider 组件包裹你的组件，并传入你的 Google 客户端ID和回调函数
        <GoogleOAuthProvider
            clientId="YOUR_GOOGLE_CLIENT_ID" // 请确保替换为你的实际 Google 客户端 ID
            onSuccess={handleSuccess} // 登录成功的回调
            onFailure={handleFailure} // 登录失败的回调
        >
            <LoginButton /> {/* 渲染登录按钮组件 */}
        </GoogleOAuthProvider>
    );
};

export default LoginComponent;
