import React, { useState, useEffect, useRef } from "react";
import EasyMDE from "easymde";
import hljs from "highlight.js";
import "highlight.js/styles/github.css";
import "easymde/dist/easymde.min.css";

// 設定 marked 的選項
marked.setOptions({
  renderer: new marked.Renderer(),
  highlight: function (code, language) {
    const validLanguage = hljs.getLanguage(language) ? language : "plaintext";
    return hljs.highlight(validLanguage, code).value;
  },
});

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);

  useEffect(() => {
    if (!easyMDEInstance.current) {
      easyMDEInstance.current = new EasyMDE({
        element: document.getElementById("easy-mde"),
        autoDownloadFontAwesome: true,
        initialValue: content,
        showIcons: ["code", "table"],
        sideBySideFullscreen: false,
        status: false,
        syncSideBySidePreviewScroll: false,
        previewRender: function (plainText, preview) {
          // Async method
          setTimeout(function () {
            preview.innerHTML = marked(plainText);
          }, 250);

          return "Loading...";
        },
      });

      easyMDEInstance.current.codemirror.on("change", () => {
        setContent(easyMDEInstance.current.value());
      });
    }

    return () => {
      if (easyMDEInstance.current) {
        easyMDEInstance.current.toTextArea();
        easyMDEInstance.current = null;
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  return (
    <div className="prose max-w-screen-xl mx-auto mt-4 pt-14">
      <form onSubmit={handleSubmit}>
        <textarea id="easy-mde"></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
