    },
    onFailure: (error) => {
      console.error("Login failed", error);
      // Ê≠§Â§ÑÂèØ‰ª•Â§ÑÁêÜÁôªÂΩïÂ§±Ë¥•Ôºå‰æãÂ¶ÇÂºπÂá∫ÈîôËØØÊèêÁ§∫
    },
    flow: "auth-code", // ‰ΩøÁî® "auth-code" ÊµÅÁ®ã
  });

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      {/* ÂΩìÊåâÈíÆÁÇπÂáªÊó∂Ëß¶Âèë signIn ÂáΩÊï∞ */}
      <button onClick={() => signIn()}>Sign in with Google üöÄ</button>
    </GoogleOAuthProvider>
  );
};

export default LoginComponent;
