import React, { useState, useEffect, useRef } from "react";
import EasyMDE from "easymde";
import "highlight.js/styles/github.css";
import "easymde/dist/easymde.min.css";
import hljs from "highlight.js";
import { marked } from "marked";

hljs.configure({
  // 可配置的高亮選項
  languages: ["javascript", "ruby", "python", "rust"],
});

// Configure marked
marked.setOptions({
  highlight: function (code) {
    return hljs.highlightAuto(code).value;
  },
  breaks: true,
});

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    easyMDEInstance.current = new EasyMDE({
      element: textareaRef.current,
      autoDownloadFontAwesome: true,
      initialValue: content,
      toolbar: [
        "undo",
        "redo",
        "|",
        "bold",
        "italic",
        "strikethrough",
        "heading",
        "heading-smaller",
        "heading-bigger",
        "|",
        "code",
        "quote",
        "unordered-list",
        "ordered-list",
        "|",
        "link",
        // "image",
        "upload-image",
        "table",
        "horizontal-rule",
        // {
        //   name: "custom",
        //   action: function customFunction(editor) {
        //     // 自定义按钮的功能
        //     console.log("Custom button clicked!");
        //   },
        //   className: "fa fa-star",
        //   title: "Custom Button",
        // },
        "|",
        "preview",
        "side-by-side",

        "|",
        "guide",
        // 注意这里没有包含 'fullscreen' 按钮
      ],
      showIcons: ["code", "table"],
      sideBySideFullscreen: false,
      status: false,
      syncSideBySidePreviewScroll: false,
      previewRender: function (plainText) {
        // 使用 marked 進行 markdown 到 HTML 的轉換
        return marked(plainText);
      },
    });

    easyMDEInstance.current.codemirror.on("change", () => {
      setContent(easyMDEInstance.current.value());
    });

    return () => easyMDEInstance.current.toTextArea();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  const markdownString = `
  \`\`\`javascript
  console.log("Hello, world!");
  \`\`\`
  `;

  const htmlContent = marked(markdownString);

  document.getElementById('content').textContent = htmlContent;

  // 在控制台输出转换后的 HTML，以便检查
  console.log(htmlContent);

  return (
    <div className="prose max-w-screen-xl mx-auto mt-4 pt-14">
      <form onSubmit={handleSubmit}>
        <textarea ref={textareaRef}></textarea>
        <div id="content"></div>
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
