import React, { useState, useEffect, useRef, useMemo } from "react";
import EasyMDE from "easymde";
import "highlight.js/styles/github.css";
import "easymde/dist/easymde.min.css";
import hljs from "highlight.js";
import { marked } from "marked";

hljs.configure({
  // 可配置的高亮選項
  languages: ["javascript", "ruby", "python", "rust"],
});

// Configure marked
marked.setOptions({
  highlight: function (code) {
    const highlighted = hljs.highlightAuto(code).value;
    console.log("Highlighting:", code); // Check if this gets logged with the expected code
    console.log("Highlighted:", highlighted); // Check the output
    return highlighted;
  },
  breaks: true,
});

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);
  const textareaRef = useRef(null);

  // useMemo to only re-render the marked content when `content` changes
  const renderedContent = useMemo(() => marked(content), [content]);

  // Test markdown string with code block
  const testMarkdown = `
    # This is a test

    Here is some JavaScript code:

    \`\`\`javascript
    console.log("hello");
    console.log("world");
    \`\`\`
    `;

  // Place this in your component for a one-time test
  useEffect(() => {
    const code = 'console.log("hello");';
    console.log("test:", code);
    const testElement = document.createElement("pre");
    testElement.innerHTML = `<code class="javascript">${code}</code>`;
    document.body.appendChild(testElement);
    hljs.highlightElement(testElement.querySelector("code"));
  }, []);

  useEffect(() => {
    easyMDEInstance.current = new EasyMDE({
      element: textareaRef.current,
      autoDownloadFontAwesome: true,
      initialValue: content,
      toolbar: [
        "undo",
        "redo",
        "|",
        "bold",
        "italic",
        "strikethrough",
        "heading",
        "heading-smaller",
        "heading-bigger",
        "|",
        "code",
        "quote",
        "unordered-list",
        "ordered-list",
        "|",
        "link",
        // "image",
        "upload-image",
        "table",
        "horizontal-rule",
        // {
        //   name: "custom",
        //   action: function customFunction(editor) {
        //     // 自定义按钮的功能
        //     console.log("Custom button clicked!");
        //   },
        //   className: "fa fa-star",
        //   title: "Custom Button",
        // },
        "|",
        "preview",
        "side-by-side",

        "|",
        "guide",
        // 注意这里没有包含 'fullscreen' 按钮
      ],
      sideBySideFullscreen: false,
      status: false,
      syncSideBySidePreviewScroll: false,
      renderingConfig: {
        codeSyntaxHighlighting: true,
      },
      previewRender: function (plainText) {
        console.log("Rendering Preview:", plainText);
        const renderedHtml = marked(plainText);
        console.log("Submitted Content:", renderedHtml);
        return renderedHtml;
      },
    });

    easyMDEInstance.current.codemirror.on("change", () => {
      setContent(easyMDEInstance.current.value());

      document.querySelectorAll("pre code").forEach((block) => {
        hljs.highlightElement(block);
      });
    });

    return () => easyMDEInstance.current.toTextArea();

    setContent(testMarkdown);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    // Apply highlighting whenever content updates
    document.querySelectorAll("pre code").forEach((block) => {
      hljs.highlightElement(block);
    });
  }, [content]);

  useEffect(() => {
    // ... (rest of your useEffect)

    // Set the test markdown as initial content
    setContent(testMarkdown);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  return (
    <div className="prose max-w-screen-xl mx-auto mt-4 pt-14 markdown-preview hljs">
      <form onSubmit={handleSubmit}>
        <textarea ref={textareaRef}></textarea>
        <div
          className="markdown-preview hljs"
          dangerouslySetInnerHTML={{ __html: renderedContent }}
        />
        {/* This line is for testing purposes */}
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
