import React, { useState } from "react";
import MdEditor from "react-markdown-editor-lite";
import MarkdownIt from "markdown-it";
import hljs from "highlight.js";
import "highlight.js/styles/github.css";
import "react-markdown-editor-lite/lib/index.css";

const PostComponent = () => {
  const mdParser = new MarkdownIt({
    breaks: true,
  });
  const [content, setContent] = useState("");
  const [uploadedImages, setUploadedImages] = useState([]);

  const handleImageUpload = (file, callback) => {
    const reader = new FileReader();
    reader.onload = () => {
      // TODO: 上传到S3，并获取URL
      const blobUrl = URL.createObjectURL(file);
      // 假设我们有一个函数 uploadToS3 上传到S3并返回URL
      // const imageUrl = await uploadToS3(file);
      // 使用真实的imageUrl而不是blobUrl
      setUploadedImages([...uploadedImages, blobUrl]); // 保存上传的图像以便稍后处理
      callback(blobUrl);
    };
    reader.readAsDataURL(file);
  };

  const handleEditorChange = ({ html, text }) => {
    // 更新内容状态
    setContent(text);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // TODO: 处理图像上传到S3
    // 例如，你可能需要遍历 uploadedImages 并上传每个图像
    // 然后替换内容中的Blob URL为S3 URL

    // TODO: 将最终内容和图像URL数组发送到后端
    // 这可能涉及到调用一个API将文章内容和图像的S3 URL存储在MongoDB中

    console.log("Content to submit:", content);
    console.log("Images to upload:", uploadedImages);
  };

  return (
    <div className="prose post-editor-component max-w-screen-xl mx-auto mt-4 pt-14">
      <MdEditor
        value={content}
        renderHTML={(text) => mdParser.render(text)}
        onChange={handleEditorChange}
        onImageUpload={handleImageUpload}
        className="h-5/6 overflow-y-auto"
      />
      <button
        onClick={handleSubmit}
        className="submit-button bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
      >
        Submit Post
      </button>
    </div>
  );
};

export default PostComponent;
