import React, { useState, useEffect, useRef } from "react";
import EasyMDE from "easymde";
import "highlight.js/styles/github.css";
import hljs from "highlight.js";
import "easymde/dist/easymde.min.css";
import { marked } from "marked";

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    hljs.configure({
      // 可配置的高亮選項
      languages: ["javascript", "ruby", "python", "rust"],
    });

    // Configure marked
    // marked.setOptions({
    //   highlight: function (code, lang) {
    //     // Check if a language is provided and highlight.js supports it
    //     if (lang && hljs.getLanguage(lang)) {
    //       return hljs.highlight(code, { language: lang }).value;
    //     }
    //     // Fallback to automatic highlighting
    //     return hljs.highlightAuto(code).value;
    //   },
    //   breaks: true,
    // });
  }, []);

  useEffect(() => {
    easyMDEInstance.current = new EasyMDE({
      element: textareaRef.current,
      autoDownloadFontAwesome: true,
      initialValue: content,
      toolbar: [
        "undo",
        "redo",
        "|",
        "bold",
        "italic",
        "strikethrough",
        "heading",
        "heading-smaller",
        "heading-bigger",
        "|",
        "code",
        "quote",
        "unordered-list",
        "ordered-list",
        "|",
        "link",
        // "image",
        "upload-image",
        "table",
        "horizontal-rule",
        // {
        //   name: "custom",
        //   action: function customFunction(editor) {
        //     // 自定义按钮的功能
        //     console.log("Custom button clicked!");
        //   },
        //   className: "fa fa-star",
        //   title: "Custom Button",
        // },
        "|",
        "preview",
        "side-by-side",

        "|",
        "guide",
        // 注意这里没有包含 'fullscreen' 按钮
      ],
      sideBySideFullscreen: false,
      status: false,
      syncSideBySidePreviewScroll: false,
      renderingConfig: {
        codeSyntaxHighlighting: true,
      },
      previewRender: function (plainText) {
        console.log("Rendering Preview:", plainText);
        const renderedHtml = marked(plainText);
        console.log("Submitted Content:", renderedHtml);
        return renderedHtml;
      },
    });

    easyMDEInstance.current.codemirror.on("change", () => {
      setContent(easyMDEInstance.current.value());

      document.querySelectorAll("pre code").forEach((block) => {
        hljs.highlightElement(block);
      });
    });

    return () => easyMDEInstance.current.toTextArea();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    // Apply highlighting whenever content updates
    document.querySelectorAll("pre code").forEach((block) => {
      hljs.highlightElement(block);
    });
  }, [content]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  return (
    <div className=" max-w-screen-xl mx-auto mt-4 pt-14 hljs">
      <form onSubmit={handleSubmit}>
        <textarea ref={textareaRef} className=""></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
