import React, { useEffect, useState } from "react";
import axios from "axios";

const LoginComponent = () => {
  const [clientId, setClientId] = useState("");

  useEffect(() => {
    // 當組件加載時，從後端獲取 Google 客戶端 ID。
    axios
      .get("http://localhost:8080/auth/config/google")
      .then((response) => {
        setClientId(response.data.clientId);
      })
      .catch((error) => {
        console.error("錯誤發生: ", error);
      });
  }, []);

  const handleLogin = () => {
    // 重定向到 Google 的 OAuth 2.0 登錄頁面。
    window.location.href = `https://accounts.google.com/o/oauth2/v2/auth?response_type=code&client_id=${clientId}&redirect_uri=http://localhost:8080/auth/google/redirect&scope=profile%20email&access_type=offline`;
  };

  return (
    <GoogleOAuthProvider clientId="GOOGLE_CLIENT_ID">
      <GoogleLogin
        onSuccess={(credentialResponse) => {
          console.log(credentialResponse);
        }}
        onError={() => {
          console.log("Login Failed");
        }}
      />
    </GoogleOAuthProvider>
  );
};

export default LoginComponent;
