import React, { useState, useEffect } from "react";
import MdEditor from "react-markdown-editor-lite";
import MarkdownIt from "markdown-it";
import hljs from "highlight.js";
import "highlight.js/styles/github.css";
import "react-markdown-editor-lite/lib/index.css";

const PostComponent = ({ boards }) => {
  const mdParser = new MarkdownIt({
    breaks: true,
    highlight: function (str, lang) {
      if (lang && hljs.getLanguage(lang)) {
        try {
          const highlighted = hljs.highlight(str, {
            language: lang,
            ignoreIllegals: true,
          });
          return (
            '<pre class="hljs"><code>' + highlighted.value + "</code></pre>"
          );
        } catch (__) {}
      }
      return (
        '<pre class="hljs"><code>' +
        mdParser.utils.escapeHtml(str) +
        "</code></pre>"
      );
    },
  });
  const [content, setContent] = useState(localStorage.getItem("content") || "");
  const [uploadedImages, setUploadedImages] = useState([]);
  const [selectedBoard, setSelectedBoard] = useState("");

  useEffect(() => {
    const contentFromStorage = localStorage.getItem("content");
    if (contentFromStorage) {
      setContent(contentFromStorage);
    }
  }, []);

  const handleImageUpload = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        const blobUrl = URL.createObjectURL(file);
        setUploadedImages([...uploadedImages, blobUrl]); // 保存上传的图像以便稍后处理
        resolve(blobUrl); // 解析 Promise 与 blob URL
      };
      reader.onerror = reject; // 在读取失败时拒绝 Promise
      reader.readAsDataURL(file);
    });
  };

  const handleEditorChange = ({ html, text }) => {
    setContent(text);
    localStorage.setItem("content", text);
  };

  const uploadImageToS3 = async (file) => {
    console.log("前端上傳到s3", file);
    const response = await fetch(
      `/api/article/get-presigned-url?fileName=${file.name}&fileType=${file.type}`
    );
    console.log("前端回覆", response);
    const { url: presignedUrl } = await response.json();

    await fetch(presignedUrl, {
      method: "PUT",
      body: file,
      headers: {
        "Content-Type": file.type,
      },
    });

    return presignedUrl.split("?")[0];
  };

  // 提交文章
  const handleSubmit = async (e) => {
    e.preventDefault();

    let updatedContent = content;

    for (const image of uploadedImages) {
      console.log()
      const s3Url = await uploadImageToS3(image.file); // 假设这个函数上传图片并返回 S3 URL
      updatedContent = updatedContent.replace(image.blobUrl, s3Url);
    }

    const articleData = {
      board: selectedBoard,
      content: updatedContent,
    };

    console.log("Article Data: ", articleData);
  };

  return (
    <div className="prose post-editor-component max-w-screen-xl mx-auto mt-4 pt-14">
      <MdEditor
        value={content}
        renderHTML={(text) => mdParser.render(text)}
        onChange={handleEditorChange}
        onImageUpload={handleImageUpload}
        config={{
          view: {
            menu: true,
            md: true,
            html: true,
            fullScreen: false,
            hideMenu: true,
          },
        }}
        className="h-[40rem] overflow-y-auto"
      />
      <select
        value={selectedBoard}
        onChange={(e) => setSelectedBoard(e.target.value)}
        className="m-2 border-spacing-0"
      >
        <option value="">選擇一個看板</option>
        {boards.map((board) => (
          <option key={board._id} value={board._id}>
            {board.name}
          </option>
        ))}
      </select>
      <button
        onClick={handleSubmit}
        className="submit-button bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
      >
        Submit
      </button>
    </div>
  );
};

export default PostComponent;
