import React, { useState, useEffect, useRef } from "react";
import EasyMDE from "easymde";
import "easymde/dist/easymde.min.css";

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);

  useEffect(() => {
    easyMDEInstance.current = new EasyMDE({
      element: document.getElementById("easy-mde"),
      autoDownloadFontAwesome: true,
      initialValue: content, // 设置初始值
      showIcons: ["code", "table"],
      sideBySideFullscreen: false,
      status: false, // Disable the status bar
      syncSideBySidePreviewScroll: false, // Disable sync scrolling
    });

    const handleEasyMDEChange = () => {
      setContent(easyMDEInstance.current.value());
    };

    mde.codemirror.on("change", handleEasyMDEChange);

    return () => {
      mde.codemirror.off("change", handleEasyMDEChange);
      easyMDEInstance.current.toTextArea();
      easyMDEInstance.current = null;
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  return (
    <div className="max-w-screen-xl mx-auto mt-4 pt-14">
      <form onSubmit={handleSubmit}>
        <textarea id="easy-mde"></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
