import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginComponent = () => {
  const [clientId, setClientId] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // 當組件加載時，從後端獲取 Google 客戶端 ID。
    axios
      .get("http://localhost:3005/auth/config/google")
      .then((response) => {
        setClientId(response.data.clientId);
      })
      .catch((error) => {
        console.error("錯誤發生: ", error);
      });

    if (result.token) {
      localStorage.setItem("authToken", result.token);
      // 為了調試，檢查 token 是否已經保存
      const storedToken = localStorage.getItem("authToken");
      console.log(storedToken); // 應該輸出您保存的 token
      navigate("/"); // token已經保存，重定向到主頁
    }
  }, [navigate]); // 依賴於 location，當 location 改變時，此 useEffect 將重新運行

  const handleLogin = () => {
    // 重定向到 Google 的 OAuth 2.0 登錄頁面。
    window.location.href = "http://localhost:3005/auth/google";
  };

  return (
    <div>
