import React from "react";
import { GoogleOAuthProvider, useGoogleLogin } from "@react-oauth/google";

const LoginButton = () => {
  const { signIn } = useGoogleLogin({
    onSuccess: (response) => {
      console.log("Login successful", response);
      // Ê≠§Â§ÑÂèØ‰ª•ËøõË°åÂêéÁª≠Êìç‰ΩúÔºå‰æãÂ¶ÇÁä∂ÊÄÅÊõ¥Êñ∞„ÄÅÈáçÂÆöÂêëÁ≠â
    },
    onFailure: (error) => {
      console.error("Login failed", error);
      // Ê≠§Â§ÑÂèØ‰ª•Â§ÑÁêÜÁôªÂΩïÂ§±Ë¥•Ôºå‰æãÂ¶ÇÂºπÂá∫ÈîôËØØÊèêÁ§∫
    },
    flow: "auth-code", // ‰ΩøÁî® "auth-code" ÊµÅÁ®ã
  });

  return <button onClick={signIn}>Sign in with Google üöÄ</button>;
};

const LoginComponent = () => {
  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <LoginButton />
    </GoogleOAuthProvider>
  );
};

export default LoginComponent;
