import React, { useState, useEffect } from 'react';
import axios from "axios";

const CardComponent = (props) => {
  const [pairedUser, setPairedUser] = useState(null);

  useEffect(() => {
    async function fetchPair() {
      try {
        const response = await axios.get(`/api/card/getPairs/${props.userId}`);
        const cards = response.data;
        const card = cards.find(
          (card) =>
            card.userID1._id === props.userId ||
            card.userID2._id === props.userId
        );
        if (card) {
          const pairedUserData =
            card.userID1._id === props.userId ? card.userID2 : card.userID1;
          setPairedUser(pairedUserData);
        }
      } catch (error) {
        console.error("Error fetching pair:", error);
      }
    }

    fetchPair();
  }, [props.userId]);

  return <div>CardComponent</div>;
};

export default CardComponent;
