import React, { useState } from "react";
import axios from "axios";
import AuthService from "../services/auth.service";


const LoginComponent = ({ currentUser, setCurrentUser }) => {
  const [clientId, setClientId] = useState("");
  let [message, setMessage] = useState("");

  // 當組件加載時，從後端獲取 Google 客戶端 ID。
  axios
    .get("http://localhost:3005/auth/config/google")
    .then((response) => {
      setClientId(response.data.clientId);
    })
    .catch((error) => {
      console.error("錯誤發生: ", error);
    });

  const handleLogin = async() => {
    // 重定向到 Google 的 OAuth 2.0 登錄頁面。
    try {
        let response = await AuthService.login();
        localStorage.setItem("user", JSON.stringify(response.data));
        window.alert("登入成功，您現在將被重新導向到個人資料頁面");
        setCurrentUser(AuthService.getCurrentUser)
        navigate("/");
      } catch (e) {
        setMessage(e.response.data);
      }
  };

  return (
    <div>
      {clientId ? (
        <button
          className="btn btn-lg btn-google" // 注意：應該使用 className 而不是 class
          style={{
            padding: "0.2rem 0.5rem",
            backgroundColor: "rgb(7, 7, 7)",
            color: "rgb(255, 255, 255)",
          }}
          onClick={handleLogin} // 確保 onClick 被正確設置
        >
          <img
            src="https://img.icons8.com/color/16/000000/google-logo.png"
            alt="Google logo"
          />
          使用 Google 登入
        </button>
      ) : (
        <div>加載中...</div>
      )}
    </div>
  );
};

export default LoginComponent;
