// 先隱藏高亮效果
import React, { useState, useEffect, useRef } from "react";
import EasyMDE from "easymde";
// import "highlight.js/styles/github.css";
// import hljs from "highlight.js";
import "easymde/dist/easymde.min.css";
import { marked } from "marked";

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);
  const textareaRef = useRef(null);

  // 用來處理圖片上傳的函數
  const handleImageUpload = (file, onSuccess, onError) => {
    try {
      const blobUrl = URL.createObjectURL(file);
      console.log("前端blobUrl", blobUrl);
      onSuccess(blobUrl);
    } catch (e) {
      console.log("Error during image upload", e);
    }
  };

  useEffect(() => {
    easyMDEInstance.current = new EasyMDE({
      autofocus: true,
      autosave: {
        enabled: true,
        uniqueId: "MyUniqueID",
        delay: 1000,
        submit_delay: 5000,
        timeFormat: {
          locale: "en-US",
          format: {
            year: "numeric",
            month: "long",
            day: "2-digit",
            hour: "2-digit",
            minute: "2-digit",
          },
        },
        text: "Autosaved: ",
      },
      element: textareaRef.current,
      autoDownloadFontAwesome: true,
      initialValue: content,
      toolbar: [
        "undo",
        "redo",
        "|",
        "bold",
        "italic",
        "strikethrough",
        "heading",
        "heading-smaller",
        "heading-bigger",
        "|",
        "code",
        "quote",
        "unordered-list",
        "ordered-list",
        "|",
        "link",
        // "image",
        "upload-image",
        "table",
        "horizontal-rule",
        // {
        //   name: "custom",
        //   action: function customFunction(editor) {
        //     // 自定义按钮的功能
        //     console.log("Custom button clicked!");
        //   },
        //   className: "fa fa-star",
        //   title: "Custom Button",
        // },
        "|",
        "preview",
        "side-by-side",

        "|",
        "guide",
        // 注意这里没有包含 'fullscreen' 按钮
      ],
      sideBySideFullscreen: false,
      status: false,
      syncSideBySidePreviewScroll: false,
      // renderingConfig: {
      //   codeSyntaxHighlighting: true,
      // },
      uploadImage: true,
      imageUploadFunction: (file, onSuccess, onError) => {
        handleImageUpload(file, onSuccess, onError);
      },
      element: textareaRef.current,
      previewRender: function (plainText, preview) {
        // Async method
        setTimeout(() => {
          preview.innerHTML = this.parent.markdown(plainText);
        }, 250);

        return preview.innerHTML;
      },
      // 配置marked的渲染器来正确处理blob URLs
      markdown: function (text) {
        const renderer = new marked.Renderer();
        renderer.image = function (href, title, text) {
          // 检查href是否为blob URL
          if (href.startsWith("blob:")) {
            // 返回自定义的img标签
            return `<img src="${href}" alt="${text}"${
              title ? ` title="${title}"` : ""
            }>`;
          } else {
            // 如果不是blob URL，使用默认的渲染方法
            return `<img src="${href}" alt="${text}"${
              title ? ` title="${title}"` : ""
            }>`;
          }
        };
        // 使用自定义渲染器来渲染Markdown
        return marked(text, { renderer: renderer });
      },
    });

    easyMDEInstance.current.codemirror.on("change", () => {
      setContent(easyMDEInstance.current.value());

      // document.querySelectorAll("pre code").forEach((block) => {
      //   hljs.highlightElement(block);
      // });
    });

    return () => easyMDEInstance.current.toTextArea();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    // hljs.configure({
    //   // 可配置的高亮選項
    //   languages: ["javascript", "ruby", "python", "rust"],
    // });

    // Configure marked
    marked.setOptions({
      breaks: true,
    });
  }, []);

  // useEffect(() => {
  //   // Apply highlighting whenever content updates
  //   document.querySelectorAll("pre code").forEach((block) => {
  //     hljs.highlightElement(block);
  //   });
  // }, [content]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  return (
    <div className=" max-w-screen-xl mx-auto mt-4 pt-14 ">
      <form onSubmit={handleSubmit}>
        <textarea
          ref={textareaRef}
          className="prose"
          id="your-textarea-id"
        ></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
