import React from "react";
import moment from "moment";

const ArticleModal = ({ article, onClose }) => {
  if (!article) return null;

  // 防止点击模态框内容时事件冒泡到覆盖层
  const handleModalContainerClick = (event) => {
    event.stopPropagation();
  };

  const formattedDate = moment(article.createdAt).format("YYYY-MM-DD HH:mm:ss");

  return (
    <>
      <div
        className="fixed inset-0 bg-black bg-opacity-50 z-10"
        onClick={onClose}
      ></div>

      <div
        className="fixed inset-0 z-20 flex items-center justify-center p-4"
        onClick={onClose}
      >
        <div
          className="w-3/4 h-3/4 bg-white p-6 rounded shadow-lg relative"
          onClick={handleModalContainerClick}
        >
          <button
            onClick={onClose}
            className="absolute top-0 right-2 mt-2 mr-2 text-black hover:text-gray-700"
          >
            <span className="text-2xl">&times;</span>
          </button>
          {/* 文章內容 */}
          <h2 className="text-xl font-semibold mb-4">{article.title}</h2>
          <div>作者：{article.author?.name}</div>
          <div>看版：{article.board?.name}</div>
          <div>時間：{formattedDate}</div>
          <hr className="my-4"></hr>
          <div>

          </div>

        </div>
      </div>
    </>
  );
};

export default ArticleModal;
