import React, { useState, useRef } from "react";
import EasyMDE from "easymde";
import "easymde/dist/easymde.min.css";
import { marked } from "marked";

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);
  const textareaRef = useRef(null);

  // 用來處理圖片上傳的函數
  const handleImageUpload = (file, onSuccess, onError) => {
    try {
      const blobUrl = URL.createObjectURL(file);
      console.log("前端blobUrl", blobUrl);
      onSuccess(`![image](${blobUrl})`);
    } catch (e) {
      console.log("Error during image upload", e);
      if (onError) {
        onError(e.toString());
      }
    }
  };

  useEffect(() => {
    easyMDEInstance.current = new EasyMDE({
      autofocus: true,
      autosave: {
        enabled: true,
        uniqueId: "MyUniqueID",
        delay: 1000,
        submit_delay: 5000,
        timeFormat: {
          locale: "en-US",
          format: {
            year: "numeric",
            month: "long",
            day: "2-digit",
            hour: "2-digit",
            minute: "2-digit",
          },
        },
        text: "Autosaved: ",
      },
      element: textareaRef.current,
      placeholder: "Type here...",
      autoDownloadFontAwesome: true,
      minHeight: "500px",
      initialValue: content,
      toolbar: [
        "undo",
        "redo",
        "|",
        "bold",
        "italic",
        "strikethrough",
        "heading",
        "heading-smaller",
        "heading-bigger",
        "|",
        "code",
        "quote",
        "unordered-list",
        "ordered-list",
        "|",
        "link",
        // "image",
        "upload-image",
        "table",
        "horizontal-rule",
        // {
        //   name: "custom",
        //   action: function customFunction(editor) {
        //     // 自定义按钮的功能
        //     console.log("Custom button clicked!");
        //   },
        //   className: "fa fa-star",
        //   title: "Custom Button",
        // },
        "|",
        "preview",
        "side-by-side",

        "|",
        "guide",
        // 注意这里没有包含 'fullscreen' 按钮
      ],
      sideBySideFullscreen: false,
      status: false,
      syncSideBySidePreviewScroll: false,

      imageUploadFunction: (file, onSuccess, onError) => {
        handleImageUpload(file, onSuccess, onError);
        console.log("歐歐", handleImageUpload);
      },
      previewRender: function (plainText) {
        const renderer = new marked.Renderer();
        renderer.image = function (href, title, text) {
          // 修复：这里直接返回正确格式的<img>标签
          console.log("嗨嗨");
          return `<img src="${href}" alt="${text}"${
            title ? ` title="${title}"` : ""
          }>`;
        };

        // 如果您使用的marked版本是0.7.0或更高，请使用这种方式设置选项
        marked.setOptions({
          renderer,
          // Other options you might want to configure
          gfm: true,
          breaks: true,
        });

        // 使用自定义渲染器来渲染Markdown
        return marked(plainText);
      },
    });

    easyMDEInstance.current.codemirror.on("change", () => {
      setContent(easyMDEInstance.current.value());
    });

    return () => easyMDEInstance.current.toTextArea();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  return (
    <div className=" max-w-screen-xl mx-auto mt-4 pt-14 ">
      <form onSubmit={handleSubmit}>
        <textarea ref={textareaRef} className="prose"></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
