// 先隱藏高亮效果
import React, { useState, useEffect, useRef } from "react";
import EasyMDE from "easymde";
// import "highlight.js/styles/github.css";
// import hljs from "highlight.js";
import "easymde/dist/easymde.min.css";
import { marked } from "marked";

const PostComponent = () => {
  const [content, setContent] = useState("");
  const easyMDEInstance = useRef(null);
  const textareaRef = useRef(null);

  // 照片檔案上傳s3
  const UPLOAD_API_ENDPOINT = "/api/article";

  // 用來處理圖片上傳的函數
  const handleImageUpload = (file, onSuccess, onError) => {
    // 創建FormData物件並附加檔案
    const formData = new FormData();
    formData.append("image", file);

    // 發送檔案到後端API
    fetch(UPLOAD_API_ENDPOINT, {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success && data.url) {
          // 呼叫 onSuccess 並提供在 S3 上的檔案 URL
          onSuccess(data.url);
        } else {
          // 如果有錯誤，呼叫 onError
          onError(data.error || "檔案上傳失敗");
        }
      })
      .catch((error) => {
        onError(error.message || "上傳時發生錯誤");
      });
  };

  useEffect(() => {
    easyMDEInstance.current = new EasyMDE({
      autosave: {
        enabled: true,
        uniqueId: "MyUniqueID",
        delay: 1000,
        submit_delay: 5000,
        timeFormat: {
          locale: "en-US",
          format: {
            year: "numeric",
            month: "long",
            day: "2-digit",
            hour: "2-digit",
            minute: "2-digit",
          },
        },
        text: "Autosaved: ",
      },
      element: textareaRef.current,
      autoDownloadFontAwesome: true,
      initialValue: content,
      toolbar: [
        "undo",
        "redo",
        "|",
        "bold",
        "italic",
        "strikethrough",
        "heading",
        "heading-smaller",
        "heading-bigger",
        "|",
        "code",
        "quote",
        "unordered-list",
        "ordered-list",
        "|",
        "link",
        // "image",
        "upload-image",
        "table",
        "horizontal-rule",
        // {
        //   name: "custom",
        //   action: function customFunction(editor) {
        //     // 自定义按钮的功能
        //     console.log("Custom button clicked!");
        //   },
        //   className: "fa fa-star",
        //   title: "Custom Button",
        // },
        "|",
        "preview",
        "side-by-side",

        "|",
        "guide",
        // 注意这里没有包含 'fullscreen' 按钮
      ],
      sideBySideFullscreen: false,
      status: false,
      syncSideBySidePreviewScroll: false,
      // renderingConfig: {
      //   codeSyntaxHighlighting: true,
      // },
      uploadImage: true,
      imageUploadFunction: (file, onSuccess, onError) => {
        handleImageUpload(file, onSuccess, onError);
      },
      previewRender: function (plainText) {
        const renderedHtml = marked(plainText);
        console.log("Submitted Content:", renderedHtml);
        return renderedHtml;
      },
    });

    easyMDEInstance.current.codemirror.on("change", () => {
      setContent(easyMDEInstance.current.value());

      // document.querySelectorAll("pre code").forEach((block) => {
      //   hljs.highlightElement(block);
      // });
    });

    return () => easyMDEInstance.current.toTextArea();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    // hljs.configure({
    //   // 可配置的高亮選項
    //   languages: ["javascript", "ruby", "python", "rust"],
    // });

    // Configure marked
    marked.setOptions({
      breaks: true,
    });
  }, []);

  // useEffect(() => {
  //   // Apply highlighting whenever content updates
  //   document.querySelectorAll("pre code").forEach((block) => {
  //     hljs.highlightElement(block);
  //   });
  // }, [content]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(content);
  };

  return (
    <div className=" max-w-screen-xl mx-auto mt-4 pt-14 ">
      <form onSubmit={handleSubmit}>
        <textarea ref={textareaRef} className="prose"></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white py-1 px-2 mt-5 rounded-lg"
        >
          submit
        </button>
      </form>
    </div>
  );
};

export default PostComponent;
